		todo!()
    }
}

impl BloodType {
	pub fn can_receive_from(self, other: Self) -> bool {
		todo!()
	}

	pub fn donors(self) -> Vec<Self> {
		todo!()
	}

	pub fn recipients(self) -> Vec<Self> {
		todo!()
	}
}
```

