```rust
use macro_map::hash_map;
use std::collections::HashMap;

fn main() {
    let empty: HashMap<u32, u32> = hash_map![];
    println!("{:?}", empty);
    println!("{:?}", hash_map!['a' => 22, 'b' => 1, 'c' => 10]);
    println!(
        "{:?}",
        hash_map![
            "first" => hash_map![
                "Rob" => 32.2,
                "Gen" => 44.1,
